// ── PARSE UTILITIES ──────────────────────────────────────────────────
const px = v => { const n = parseFloat(String(v||"").replace(/[^0-9.]/g,"")); return isNaN(n)?null:n; };
const pct= v => { const n = parseFloat(String(v||"")); return isNaN(n)?null:n; };
const cnt= v => { const n = parseInt(String(v||"").replace(/,/g,"")); return isNaN(n)?0:n; };

// ── CLEAN PRODUCTS AT MODULE LEVEL ───────────────────────────────────
const ALL = RAW.map(r => ({
  name: r.n,
  cat: r.c,
  main: r.c.split("|")[0],
  subs: r.c.split("|").slice(1),
  leaf: r.c.split("|").slice(-1)[0],
  price: px(r.dp),
  actual: px(r.ap),
  disc: pct(r.disc),
  rating: parseFloat(r.r)||null,
  count: cnt(r.rc),
  about: r.ab||"",
  reviewTitle: r.rt||"",
  searchText: (r.n+" "+r.c+" "+(r.ab||"")+" "+(r.rt||"")).toLowerCase(),
})).filter(p => p.price && p.rating && p.price > 0);

