function searchProducts(query) {
  const q = query.toLowerCase().trim();
  if (!q) return [];
  
  // Get expanded tokens (including synonyms)
  const tokens = [q, ...(SYNONYMS[q]||[])];
  // Also add individual words from the query
  q.split(/\s+/).forEach(w => { if (w.length>2) tokens.push(w); });
  
  const scored = ALL.map(p => {
    let score = 0;
    tokens.forEach(tok => {
      if (p.name.toLowerCase().includes(tok)) score += 3;
      if (p.leaf.toLowerCase().includes(tok)) score += 2;
      if (p.cat.toLowerCase().includes(tok)) score += 2;
      if (p.searchText.includes(tok)) score += 1;
    });
    return { ...p, score };
  }).filter(p => p.score > 0);

  return scored.sort((a,b) => b.score - a.score);
}

// ── STOPWORDS ────────────────────────────────────────────────────────
const STOPS = new Set(["the","a","an","and","or","in","on","at","to","for","of","with","by","is","are","was","be","have","has","do","does","this","that","it","its","i","you","we","they","not","no","very","more","all","any","per","use","used","using","good","great","best","new","high","low","get","made","make","set","type","pack","compatible","device","devices","product","products","supports","support","also","from","than","just","each","other","such","as","if","up","out","about","which","can","will","your","our","its","may","been","were","had","has","would","could","should","work","works","working","come","comes","comes","fast","easy","need","needs","make","made","takes","take"]);

function extractKeywords(texts, n=15) {
  const freq = {};
  texts.forEach(t => {
    String(t).toLowerCase().replace(/[^a-z0-9\s]/g," ").split(/\s+/)
      .filter(w => w.length>2 && !STOPS.has(w) && isNaN(w))
      .forEach(w => { freq[w]=(freq[w]||0)+1; });
  });
  return Object.entries(freq).sort((a,b)=>b[1]-a[1]).slice(0,n).map(([w,c])=>({w,c}));
}

// ── SENTIMENT (simple rule-based) ────────────────────────────────────
const POS_WORDS = new Set(["good","great","excellent","best","amazing","perfect","love","fantastic","superb","outstanding","nice","quality","value","recommend","satisfied","happy","works","fast","durable","reliable","solid","strong","bright","clear","smooth","easy","comfortable"]);
const NEG_WORDS = new Set(["bad","poor","worst","terrible","awful","horrible","broken","useless","waste","disappointing","defective","cheap","fake","wrong","slow","fail","failed","issue","problem","not working","stopped","damaged"]);

function sentimentScore(text) {
  const words = text.toLowerCase().split(/\s+/);
  let pos=0,neg=0;
  words.forEach(w=>{ if(POS_WORDS.has(w))pos++; if(NEG_WORDS.has(w))neg++; });
  if(pos+neg===0) return 0;
  return (pos-neg)/(pos+neg);
}

