// ── PRICE DISTRIBUTION ANALYSIS ──────────────────────────────────────
function priceDistribution(matches) {
  const sorted = matches.map(p=>p.price).filter(Boolean).sort((a,b)=>a-b);
  if (sorted.length < 5) return null;
  const min=sorted[0], max=sorted[sorted.length-1];
  const range = max-min;
  const bucketSize = range/6;
  const buckets = Array.from({length:6},(_,i)=>{
    const lo=min+i*bucketSize, hi=lo+bucketSize;
    const items = matches.filter(p=>p.price>=lo&&p.price<(i===5?hi+1:hi));
    const ar = items.length ? items.reduce((s,p)=>s+(p.rating||0),0)/items.length : 0;
    return { range:`₹${Math.round(lo/100)*100}–${Math.round(hi/100)*100}`, count:items.length, ar:parseFloat(ar.toFixed(2)) };
  }).filter(b=>b.count>0);
  return buckets;
}

// ── MAIN ANALYZE FUNCTION ────────────────────────────────────────────
function analyze(query) {
  const matches = searchProducts(query);
  if (matches.length < 5) return null;

  // Score-weighted top products
  const byPopularity = [...matches].sort((a,b)=>(b.rating*Math.log1p(b.count))-(a.rating*Math.log1p(a.count)));
  const top20 = byPopularity.slice(0,20);
  const top5 = byPopularity.slice(0,5);

  // ── KEYWORDS
  const kwTexts = top20.map(p=>p.name+" "+p.about+" "+p.reviewTitle);
  const keywords = extractKeywords(kwTexts, 15);

  // ── SENTIMENT ANALYSIS on reviews
  const sentiments = matches.map(p=>({
    name: p.name.slice(0,30),
    score: parseFloat(sentimentScore(p.reviewTitle).toFixed(2)),
    rating: p.rating,
  }));
  const avgSentiment = sentiments.reduce((s,x)=>s+x.score,0)/sentiments.length;
  const posCount = sentiments.filter(x=>x.score>0.1).length;
  const negCount = sentiments.filter(x=>x.score<-0.1).length;
  const neuCount = sentiments.length - posCount - negCount;

  // ── REGIONS
  const mainCat = matches[0].main;
  const regions = REGIONS[mainCat] || REGIONS["Electronics"];

  // ── PRICE TIERS
  const prices = matches.map(p=>p.price).filter(Boolean).sort((a,b)=>a-b);
  const p25=prices[Math.floor(prices.length*0.25)];
  const p50=prices[Math.floor(prices.length*0.5)];
  const p75=prices[Math.floor(prices.length*0.75)];
  const tierDefs = [
    {name:"Budget",lo:0,hi:p25},
    {name:"Economy",lo:p25,hi:p50},
    {name:"Mid-range",lo:p50,hi:p75},
    {name:"Premium",lo:p75,hi:Infinity},
  ];
  const tierData = tierDefs.map(t=>{
    const items = matches.filter(p=>p.price>=t.lo&&p.price<t.hi);
    if(!items.length) return null;
    const ar = items.reduce((s,p)=>s+(p.rating||0),0)/items.length;
    const ac = items.reduce((s,p)=>s+p.count,0)/items.length;
    const ad = items.filter(p=>p.disc!==null).reduce((s,p)=>s+p.disc,0)/items.filter(p=>p.disc!==null).length;
    const mn=Math.min(...items.map(p=>p.price)), mx=Math.max(...items.map(p=>p.price));
    return {name:t.name,count:items.length,ar:parseFloat(ar.toFixed(2)),ac:Math.round(ac),ad:Math.round(ad||0),mn,mx,score:ar*Math.log1p(ac)};
  }).filter(Boolean);
  const bestTier = tierData.length ? [...tierData].sort((a,b)=>b.score-a.score)[0] : null;

  // ── PRICE DISTRIBUTION CHART
  const priceDist = priceDistribution(matches);

  // ── DISCOUNT ANALYSIS
  const discItems = matches.filter(p=>p.disc!==null);
  const dBuckets = [
    {range:"0–20%",lo:0,hi:20},
    {range:"20–40%",lo:20,hi:40},
    {range:"40–60%",lo:40,hi:60},
    {range:"60–80%",lo:60,hi:80},
    {range:"80%+",lo:80,hi:101},
  ].map(b=>{
    const items = discItems.filter(p=>p.disc>=b.lo&&p.disc<b.hi);
    const ar = items.length ? items.reduce((s,p)=>s+(p.rating||0),0)/items.length : 0;
    return {range:b.range,count:items.length,ar:parseFloat(ar.toFixed(2)),pct:Math.round(items.length/matches.length*100)};
  });
  const avgDisc = discItems.length ? Math.round(discItems.reduce((s,p)=>s+p.disc,0)/discItems.length) : 0;
  const strategy = avgDisc>55?"Heavy Discount":avgDisc>35?"Discount-Driven":avgDisc>20?"Value-Driven":"Premium";
  const bestBucket = [...dBuckets].filter(b=>b.ar>=3.8).sort((a,b)=>b.count-a.count)[0];

  // ── RATING DISTRIBUTION
  const ratingDist = [
    {stars:"5★",count:matches.filter(p=>p.rating>=4.8).length},
    {stars:"4–5★",count:matches.filter(p=>p.rating>=4&&p.rating<4.8).length},
    {stars:"3–4★",count:matches.filter(p=>p.rating>=3&&p.rating<4).length},
    {stars:"<3★",count:matches.filter(p=>p.rating<3).length},
  ];

  // ── COMPETITION SCORE
  const total = matches.length;
  const avgR = matches.reduce((s,p)=>s+(p.rating||0),0)/matches.length;
  const avgC = matches.reduce((s,p)=>s+p.count,0)/matches.length;
  const pctHigh = matches.filter(p=>p.rating>=4.0).length/matches.length;
  const avgPrice = matches.reduce((s,p)=>s+(p.price||0),0)/matches.length;
  const compRaw = Math.min(total/300,1)*35 + Math.min(avgC/50000,1)*35 + pctHigh*20 + Math.min(avgR/5,1)*10;
  const compScore = Math.round(compRaw);
  const compLabel = compScore<=33?"Easy Entry":compScore<=55?"Moderate":compScore<=72?"Competitive":"Saturated";
  const compColor = compScore<=33?"#22c55e":compScore<=55?"#84cc16":compScore<=72?"#f59e0b":"#ef4444";

  // ── OPPORTUNITY INSIGHT
  const lowComp = dBuckets.find(b=>b.count>3&&b.ar>4.1);
  const gapTier = tierData.filter(t=>t.count<5&&t.count>0)[0];
  
  // ── MARKET INSIGHTS (generated text)
  const insights = [];
  if (pctHigh > 0.7) insights.push(`${Math.round(pctHigh*100)}% of products are rated 4★+, indicating high buyer satisfaction in this category.`);
  if (avgDisc > 40) insights.push(`The category runs on heavy discounting (avg ${avgDisc}%). Buyers expect deals — don\'t list at full price.`);
  if (avgDisc < 20) insights.push(`Low average discount (${avgDisc}%) suggests buyers value quality over deals. Premium positioning works here.`);
  if (avgC > 10000) insights.push(`High review volumes (avg ${Math.round(avgC).toLocaleString()} per product) mean buyers do thorough research. Listing quality matters.`);
  if (bestTier) insights.push(`The ${bestTier.name} tier (₹${Math.round(bestTier.mn).toLocaleString()}–₹${Math.round(bestTier.mx).toLocaleString()}) has the best rating-to-popularity balance.`);
  if (avgSentiment > 0.2) insights.push(`Customer sentiment is largely positive (score: +${avgSentiment.toFixed(2)}). Focus on maintaining quality standards.`);
  if (avgSentiment < 0) insights.push(`Mixed-to-negative sentiment (score: ${avgSentiment.toFixed(2)}) — there\'s room to win on quality and customer service.`);

  return {
    query, total, mainCat, avgRating:avgR, avgPrice,
    regions, tierData, bestTier, priceDist,
    dBuckets, avgDisc, strategy, bestBucket,
    keywords, ratingDist,
    sentiment: {avg:avgSentiment, pos:posCount, neg:negCount, neu:neuCount},
    compScore, compLabel, compColor,
    top5, insights,
  };
}

